import React, { Component } from "react";
import bank from "../images/logo.png";

// A fancy function to shorten someones wallet address, no need to show the whole thing.
const account = (str) => {
  return str.substring(0, 5) + "..." + str.substring(str.length - 4);
};


const Welcome = () => {
  const { connectWallet, currentAccount, formData, sendTransaction, handleChange, isLoading } = useContext(TransactionContext);
  
    return (
      <nav
        className="navbar navbar-dark fixed-top shadow p-0"
        style={{
          backgroundColor: "#505050",
          height: "60px",
          opacity: ".85",
        }}
      >
        <a
          href="/#"
          className="navbar-brand col-sm-3 col-md-2 mr-0"
          style={{ color: "white" }}
        >
          <img
            src={bank}
            width="50"
            height="50"
            className="d-inline-block align-top"
            alt="bank"
          />
          &nbsp; Credence App
        </a>
        <ul className="navbar-nav px-3">
          <li className="text-nowrap d-done nav-item d-sm-none d-sm-block">
            <medium style={{ color: "white" }}>
              Address: {account(this.props.account)}{" "}
              
            </medium>
            
          </li>
          <li></li>
          
        </ul>
        <ul className="navbar-nav px-3">
        { 
            this.props.isConnected() ? (
              <button 
                      className="btn btn-outline-light btn-md btn-block btn-white-space nowrap tr"
                            >
                                <p className="text-white text-base font-semibold"> Connected </p>
                            </button>) :(
          <button 
          type="submit"
            onClick={(event) => {
              event.preventDefault(this.props.connectWallet());
            }}
                  className="btn btn-primary btn-lg btn-block btn-white-space nowrap"
                        >
                            <p className="text-white text-base font-semibold"> Connect Wallet  </p>
                        </button>)}
        </ul>
      </nav>
    );
  }


export default Navbar;
