import React, { Component } from "react";
import tether from "./images/tether.png";
import Airdrop from "./components/Airdrop.js";

class Main extends Component {
  render() {
    return (
      <div>
        <div
          className="card mt-5 text-center"
          style={{
            borderRadius: ".5rem",
            border: "1px solid transparent",
            backgroundColor: "rgba(137, 145, 211, .7)",
            backdropFilter: "blur(0.2rem)",
            boxShadow: "1.3rem 1.3rem 1.3rem rgba(0, 0, 0, 0.1)",
            borderTopColor: "rgba(225, 225, 225, 0.5)",
            borderLeftColor: "rgba(225, 225, 225, 0.5)",
            borderBottomColor: "rgba(225, 225, 225, 0.1)",
            borderRightColor: "rgba(225, 225, 225, 0.1)",
          }}
        >
          <div class="container">
  <div class="row">
    <div class="col">
      <marquee behavior="scroll" direction="left">
        <span class="font-weight-bold text-danger">
          Click on the about tab on the menu to understand how the app works
        </span>
      </marquee>
    </div>
  </div>
</div>

        </div>
        <div id="content" className="mt-5">
          <button
            type="submit"
            onClick={(event) => {
              event.preventDefault(this.props.claimTokens());
            }}
            className="btn btn-outline-light btn-md btn-block btn-white-space nowrap tr"
          >
            Claim USDT
          </button>
          &nbsp;&nbsp;
          <table className="table text-muted text-center">
            <thead>
              <tr style={{ color: "white" }}>
                <th scope="col">Staking Balance</th>
                <th scope="col">Reward Balance</th>
              </tr>
            </thead>
            <tbody>
              <tr style={{ color: "white" }}>
                <td>
                  {window.web3.utils.fromWei(
                    this.props.stakingBalance,
                    "Ether"
                  )}{" "}
                  mUSDT
                </td>
                <td>
                  {window.web3.utils.fromWei(this.props.gainsBalance, "Ether")}{" "}
                  RWD
                </td>
              </tr>
            </tbody>
          </table>
          <div
            className="card mb-2"
            style={{
              borderRadius: ".5rem",
              border: "1px solid transparent",
              backgroundColor: "rgba(137, 145, 211, .7)",
              backdropFilter: "blur(0.2rem)",
              boxShadow: "1.3rem 1.3rem 1.3rem rgba(0, 0, 0, 0.1)",
              borderTopColor: "rgba(225, 225, 225, 0.5)",
              borderLeftColor: "rgba(225, 225, 225, 0.5)",
              borderBottomColor: "rgba(225, 225, 225, 0.1)",
              borderRightColor: "rgba(225, 225, 225, 0.1)",
            }}
          >
           <form
  onSubmit={(event) => {
    event.preventDefault();
    let amount;
    amount = this.input.value.toString();
    amount = window.web3.utils.toWei(amount, "Ether");
    this.props.stakeTokens(amount);
  }}
  className="mb-3 animate__animated animate__fadeIn"
>
  <div className="form-group mb-4 ml-0.6">
    <input
      ref={(input) => {
        this.input = input;
      }}
      type="number"
      placeholder="0"
      className="form-control form-control-lg"
      style={{
        border: "1px solid azure",
        borderRadius: ".2rem",
        height: "50px",
        fontSize: "1.2rem",
      }}
      required
    />
    <div className="input-group-append">
      <div
        className="input-group-text bg-primary text-white ml-1 animate__animated animate__fadeInRight"
        style={{
          cursor: "pointer",
          boxShadow: "2px 3px 68px -46px rgba(0, 123, 255, 1)",
          borderRadius: ".2rem",
          fontSize: "1.2rem",
        }}
      >
        <img src={tether} alt="tether" height="25" />
        &nbsp;&nbsp; mUSDT
      </div>
    </div>
  </div>
  <button
    type="submit"
    className="btn btn-primary btn-lg btn-block btn-white-space nowrap animate__animated animate__fadeIn"
    style={{ fontSize: "1.2rem" }}
  >
    Stake
  </button>
  <button
    type="submit"
    onClick={(event) => {
      event.preventDefault(this.props.unstakeTokens());
    }}
    className="btn btn-primary btn-lg btn-block btn-white-space nowrap animate__animated animate__fadeIn"
    style={{ fontSize: "1.2rem" }}
  >
    Unstake All
  </button>
</form>

            <div className="card-body text-center" style={{ color: "white" }}>
              Airdrop
              <Airdrop
                stakingBalance={this.props.stakingBalance}
                issueTokens={this.props.issueTokens}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
