import React from "react";
import {useState, useContext} from 'react'



import { TransactionContext } from "../context/TransactionContext";

const NavbarItem = ({ title, classProps }) => {
  return (
    <li className={`mx-4 cursor-pointer ${classProps}`}>
       {title}
    </li>
  )
}

const Connect = () => {
 
  const { connectWallet, currentAccount } = useContext(TransactionContext);

    return (
      <nav
      className="navbar navbar-dark fixed-top shadow p-0"
      style={{
        backgroundColor: " #505050",
        height: "30px",
        opacity: ".85",
      }}
    >
      <a
        href="/#"
        className="navbar-brand col-sm-3 col-md-2 mr-0"
        style={{ color: "white" }}
      >
        <img
          src={bank}
          width="50"
          height="50"
          className="d-inline-block align-top"
          alt="bank"
        />
        &nbsp; Credence App
      </a>
      <ul className="navbar-nav px-3">
        <li className="text-nowrap d-done nav-item d-sm-none d-sm-block">
          <medium style={{ color: "white" }}>
            Address: {account(this.props.account)}{" "}
            
          </medium>
          
        </li>
        <li></li>
        
      </ul>
      <ul className="navbar-nav px-3">
            
              {!currentAccount &&(  <button  type="submit"
            onClick={connectWallet}
                      className="btn btn-outline-light btn-md btn-block btn-white-space nowrap tr"
                            >
                                <p className="text-white text-base font-semibold"> Connect Wallet </p>
                            </button>)}{
                              currentAccount &&<button  type="submit"
                       
                                        className="btn btn-outline-light btn-md btn-block btn-white-space nowrap tr"
                                              >
                                                  <p className="text-white text-base font-semibold"> Connected </p>
                                              </button>
                            }
               
            
            
                            </ul>
        </nav>
    );
}

export default Connect;
