import React, { useContext } from "react";
import bank from "../images/neww.png";
import { shortenAddress } from "../utils/shortenAddress";
import { Link, NavLink } from "react-router-dom";

import { TransactionContext } from "../context/TransactionContext";

const Connect = () => {
  const { connectWallet, currentAccount } = useContext(TransactionContext);

  return (
    <nav
      className="navbar navbar-expand-lg navbar-dark fixed-top shadow p-0 bg-dark"
      style={{ height: "50px", opacity: ".85" }}
    >
      {/* Brand/logo */}
      <a
        href="/#"
        className="navbar-brand col-sm-3 col-md-2 mr-0"
        style={{ color: "white" }}
      >
        <img
          src={bank}
          width="110"
          height="40"
          className="d-inline-block align-top"
          alt="bank"
        />
      </a>

      {/* Mobile menu icon */}
      <button
        className="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarNav"
        aria-controls="navbarNav"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>

      {/* Menu items */}
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav mr-auto">
          {/* Home */}
          <li>
          <Link to="/">Home</Link>
        </li>

          {/* About */}
          <li>
          <NavLink to="/about">About</NavLink>
        </li>
        </ul>

        {/* Address and Connect Wallet/Connected button */}
        <ul className="navbar-nav">
          {/* Address */}
          <li className="text-nowrap nav-item d-none d-lg-block mr-3">
            <small style={{ color: "white" }}>
              Address: {shortenAddress(currentAccount)}
            </small>
          </li>

          {/* Connect Wallet/Connected button */}
          <li className="nav-item">
            {!currentAccount && (
              <button
                type="button"
                onClick={connectWallet}
                className="btn btn-info btn-sm"
              >
                Connect Wallet
              </button>
            )}
            {currentAccount && (
              <button
                type="button"
                className="btn btn-success btn-sm"
                disabled
              >
                Connected
              </button>
            )}
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Connect;
