import React, { useContext } from "react";
import bank from "../images/neww.png";
import { shortenAddress } from "../utils/shortenAddress";

import { TransactionContext } from "../context/TransactionContext";

const Connect = () => {
  const { connectWallet, currentAccount } = useContext(TransactionContext);

  return (
    <nav
      className="navbar navbar-dark fixed-top shadow p-0 bg-dark"
      style={{ height: "50px", opacity: ".85" }}
    >
      <a
        href="/#"
        className="navbar-brand col-sm-3 col-md-2 mr-0"
        style={{ color: "white" }}
      >
        <img
          src={bank}
          width="110"
          height="40"
          className="d-inline-block align-top"
          alt="bank"
        />
      </a>
      <ul className="navbar-nav px-3">
        <li className="nav-item">
          <a href="/" className="nav-link text-white">
            Home
          </a>
        </li>
        <li className="nav-item">
          <a href="/about" className="nav-link text-white">
            About
          </a>
        </li>
        <li className="text-nowrap nav-item d-none d-sm-block">
          <small style={{ color: "white" }}>
            Address: {shortenAddress(currentAccount)}
          </small>
        </li>
      </ul>
      <ul className="navbar-nav px-3">
        {!currentAccount && (
          <button
            type="button"
            onClick={connectWallet}
            className="btn btn-info btn-sm btn-block"
          >
            Connect Wallet
          </button>
        )}
        {currentAccount && (
          <button
            type="button"
            className="btn btn-success btn-sm btn-block"
            disabled
          >
            Connected
          </button>
        )}
      </ul>
    </nav>
  );
};

export default Connect;
